package com.kaban.asteroids;import javax.microedition.khronos.egl.EGLConfig;import javax.microedition.khronos.opengles.GL10;import android.app.Activity;import android.content.pm.ActivityInfo;import android.opengl.GLSurfaceView;import android.os.Bundle;import android.view.View;import android.view.Window;class Game{    static    {        System.loadLibrary("Asteroids");    }	public native static void init();	public native static void draw();	public native static void resize(int w, int h);	public native static void pause();	public native static void resume();	public native static void onButton(int b);}class Renderer implements GLSurfaceView.Renderer{	@Override	public void onDrawFrame(GL10 no_use) {Game.draw();}	@Override	public void onSurfaceCreated(GL10 no_use, EGLConfig c) {Game.init();}	@Override	public void onSurfaceChanged(GL10 no_use, int w, int h){Game.resize(w, h);}}public class Main extends Activity{	private GLSurfaceView _surface;	public void onLeft(View v)  {Game.onButton(0);}	public void onRight(View v) {Game.onButton(1);}	public void onBrake(View v) {Game.onButton(2);}	public void onAccel(View v) {Game.onButton(3);}	public void onFire(View v)  {Game.onButton(4);}	@Override public void onCreate(Bundle savedState)	{		super.onCreate(savedState);		setRequestedOrientation(ActivityInfo.SCREEN_ORIENTATION_PORTRAIT);		requestWindowFeature(Window.FEATURE_NO_TITLE);		setContentView(R.layout.main);		_surface = (GLSurfaceView) findViewById(R.id.glview);		_surface.setEGLContextClientVersion(2);		_surface.setEGLConfigChooser(true);		_surface.setRenderer(new Renderer());	}	@Override protected void onResume()	{		super.onResume();		_surface.onResume();		Game.resume();	}	@Override protected void onPause()	{		super.onPause();		_surface.onPause();		Game.pause();	}}